import { createTheme } from '@mui/material/styles';

const textFieldTheme = createTheme({
  components: {
    
    TextField: {
      styleOverrides: {
        root: {
          '& label': {
            color: '#EAECF0',
          },
          '& label.Mui-focused': {
            color: '#E4E7EC',
          },
          '& .MuiInput-underline:after': {
            borderBottomColor: '#A83E3E',
          },
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: '#07C7E9',
            },
            '&:hover fieldset': {
              borderColor: '#C6F106',
              borderWidth: '0.15rem',
            },
            '&.Mui-focused fieldset': {
              borderColor: '#FC1388',
            },
          },
        },
      },
    },
    MuiFormHelperText: {
        styleOverrides: {
          root: {
            color:'#fafafa',
            textTransform: 'initial',
            fontSize: '1rem',
          },
        },
      },
  },
});

export default textFieldTheme;
