import { createTheme } from '@mui/material/styles';

const textFieldTheme = createTheme({
 
    TextField: {
      styleOverrides: {
        root: {
            '& label': {
                color: '#A0A0A0',
            },
            '& label.Mui-focused': {
                color: '#BFBFBF',
                borderBottomColor: '#636363',
            },
            '& input ': {
                color: '#FFFFFF',
            },
            '& .MuiInput-underline:after': {
                borderBottomColor: '#636363',
            },
            '& .MuiOutlinedInput-root': {
                '& fieldset': {
                    borderColor: '#BFBFBF',
                },
                '&:hover fieldset': {
                    borderColor: '#A0A0A0',
                },
                '&.Mui-focused fieldset': {
                    borderColor: 'BFBFBF',
                },
    
            },
        },
      },
    },
   
  
});

export default textFieldTheme;
