import { colors } from "@material-ui/core";

export const button = {
  root: {
    /* "&:hover": {
            backgroundColor: "#07CEF1",
            color: "#FFFFFF",
        }, */
    /* "&:active": {
            backgroundColor: "#9FD311",
            color: "#FFFFFF",
        }, */
    /* "&:focus": {
            backgroundColor: "#F30606",
            color: "#FFFFFF",
        }, */
    "&:disabled": {
      backgroundColor: "#07FF30",
      color: "#FFFFFF",
    },
  },
  label: {
    color: "#FFFFFF",
  },
  contained: {
    backgroundColor: colors.purple[400],
    color: "#FFFFFF",
  },
  text: {
    '&:hover': {
      backgroundColor: colors.purple[400],
      
    }
  }
  
};
