export const inputLabel = { 

    root: {
        "& label": {
            color: "#BFBFBF",
        },
        "& label.Mui-focused": {
            color: "#32C08D",
            borderBottomColor: "#1100FF",
        },
        "& input ": {
            color: "#FFFFFF",
        },
        "& .MuiInput-underline:after": {
            borderBottomColor: "#FD11F1",
        },
        "& .MuiOutlinedInput-root": {
            "& fieldset": {
                borderColor: colors.gray[700],},
            "&:hover fieldset": {
                borderColor: "#FFFFFF",
            },
            "&.Mui-focused fieldset": {
                borderColor: "BFBFBF",
            },
        },
    },
    
    }
