import {Grid} from '@material-ui/core';
import {FormControl} from '@material-ui/core';
import {InputLabel} from '@material-ui/core';
import Select from '@material-ui/core/Select';
import  {makeStyles}  from '@material-ui/core/styles';
import {InputBase} from '@material-ui/core';
import {styled} from '@material-ui/core/styles';

const BootstrapInput = styled(InputBase)(({ theme }) => ({
    'label + &': {
      marginTop: theme.spacing(3),
    },
    '& .MuiInputBase-input': {
      borderRadius: 4,
      position: 'relative',
      backgroundColor: theme.palette.background.paper,
      border: '1px solid #ced4da',
      fontSize: 16,
      padding: '10px 26px 10px 12px',
      transition: theme.transitions.create(['border-color', 'box-shadow']),
      // Use the system font instead of the default Roboto font.
      fontFamily: [
        '-apple-system',
        'BlinkMacSystemFont',
        '"Segoe UI"',
        'Roboto',
        '"Helvetica Neue"',
        'Arial',
        'sans-serif',
        '"Apple Color Emoji"',
        '"Segoe UI Emoji"',
        '"Segoe UI Symbol"',
      ].join(','),
      '&:focus': {
        borderRadius: 4,
        borderColor: '#80bdff',
        boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
      },
    },
  }));

function buildSelectBox(classes, state, handleChange, text, option1, option2, option3) {
    return <Grid item>
        <FormControl variant="outlined" >
            <InputLabel htmlFor="kindofWork" variant="outlined">{text}</InputLabel>
            <Select
                nativeSS
                value={state.age}
                onChange={handleChange}
                label="kindofWork"
                input={BootstrapInput}
            >
                <option aria-label="None" value="" />
                
                <option value={10}>{option1}</option>
                <option value={20}>{option2}</option>
                <option value={30}>{option3}</option>
            </Select>
        </FormControl>
    </Grid>};
    export {buildSelectBox};
