import { createTheme } from '@mui/material/styles';

const textFieldTheme = createTheme({
  components: {
    
    TextField: {
      styleOverrides: {
        root: {
          '& label': {
            color: '#EAECF0',
          },
          '& label.Mui-focused': {
            color: '#E4E7EC',
          },
        }
          },
        },
      
    MuiFormHelperText: {
        styleOverrides: {
          root: {
            color:'#fafafa',
            textTransform: 'initial',
            fontSize: '1rem',
          },
        },
      },
  },
});

export default textFieldTheme;
