export const muitextFieldTheme = {
  
    root: {
      "& label": {
        color: "#F80808",
      },
      "& label.Mui-focused": {
        color: "#0A0EF5",
        borderBottomColor: "#1100FF",
      },
      "& input ": {
        color: "#FFFFFF",
      },
      "& .MuiInput-underline:after": {
        borderBottomColor: "#FD11F1",
      },
      "& .MuiOutlinedInput-root": {
        "& fieldset": {
          borderColor: "#DAFF0A",
        },
        "&:hover fieldset": {
          borderColor: "#0FFC07",
        },
        "&.Mui-focused fieldset": {
          borderColor: "BFBFBF",
        },
      },
    },
  
};

export default textFieldTheme;
