export const muiTextField= {
  
    root: {
      "& label": {
        color: "#BFBFBF",
      },
      "& label.Mui-focused": {
        color: "#32C",
        borderBottomColor: "#1100FF",
      },
      "& input ": {
        color: "#FFFFFF",
      },
      "& .MuiInput-underline:after": {
        borderBottomColor: "#FD11F1",
      },
      "& .MuiOutlinedInput-root": {
        "& fieldset": {
          borderColor: "#0AFF53",
        },
        "&:hover fieldset": {
          borderColor: "#0FFC07",
        },
        "&.Mui-focused fieldset": {
          borderColor: "BFBFBF",
        },
      },
    },
  
};
