import { colors } from "../colors";
export const muiInputLabel = {
  root: {
    '& label': {
        color: colors.gray[400],
    },
    '& label.Mui-focused': {
        color: colors.yellow[400],
        borderBottomColor: "#1100FF",
    },

  },
    outlined: {
        '& fieldset': {
            borderColor: colors.gray[700],
        },
        '&:hover fieldset': {
            borderColor: "#FFFFFF",
        },
        '&.Mui-focused fieldset': {
            borderColor: colors.gray[400],
        },
    },

};
