import { createTheme } from '@mui/material/styles';

const textFieldTheme = createTheme({
  components: {
    
    TextField: {
      styleOverrides: {
        root: {
          '& label': {
            color: '#EAECF0',
          },
          '& label.Mui-focused': {
            color: '#E4E7EC',
          },
        }
          },
        },
      
    MuiInput: {
        styleOverrides: {
          colorSecondary:{
            color: '#EAECF0',
          }
        },
      },
  },
});

export default textFieldTheme;
