import {  createTheme } from "@material-ui/core";
import colors from '../colors';

export const lightTheme = createTheme({
    palette: {
        primary: {
            main: colors.green[400],
            contrastText: colors.gray[50]
        },
        secondary: {
            main: colors.geekBlue[400],
        },
        tertiary: {
            main: "#7F5FFF",
            contrastText: colors.gray[50],
        },
        info: {
            main: "#7F5FFF",
        },
        warning: {
            main: colors.yellow[400],
        },

        error: {
            main: "#ff5c41",
        },
        default: {
            main: "#bfbfbf",
        },

        mode: "light",

        background: {
            default: "#fafafa",
        },
    },
});
